mapper.select:
SELECT SUM(ADD_POINT)
FROM POINT
WHERE MEMBER_NO = #{memberNo}

SELECT * FROM METO_MANAGE WHERE MENTOR_PASS = 'Y'
MEMBER_NO = #{mNo.memberNo}
ORDER BY SPEC_C_NAME DESC

SELECT * FROM METO_MANAGE WHERE MENTOR_PASS = 'C'
MEMBER_NO = #{sNo.memberNo}
ORDER BY SPEC_C_NAME DESC

SELECT DISTINCT M.MEMBER_NO, M.MEMBER_EMAIL, M.MEMBER_NICKNAME, M.MEMBER_NAME, M.POINT, COUNT(D.DECLARATION_N_NO) FROM MEMBER_MANAGE M JOIN DECLARATION D ON(M.MEMBER_NO=D.DECLARATION_N_NO) WHERE D.DECLARATION_CHECK = 'N'
D.DECLARATION_N_NO = #{dMemberList.memberNo}
GROUP BY M.MEMBER_NO, M.MEMBER_EMAIL, M.MEMBER_NICKNAME, M.MEMBER_NAME, M.POINT ORDER BY COUNT(D.DECLARATION_N_NO) DESC

SELECT DECLARATION_N_NO MEMBER_NO, COUNT(DECLARATION_N_NO) AS TOTALCOUNT FROM DECLARATION
DECLARATION_N_NO = #{dMemberList.memberNo}
GROUP BY DECLARATION_N_NO

SELECT DECLARATION_N_NO MEMBER_NO, COUNT(DECLARATION_N_NO) AS DCOUNT FROM DECLARATION WHERE DECLARATION_CHECK = 'N'
DECLARATION_N_NO = #{dMemberList.memberNo}
GROUP BY DECLARATION_N_NO

mapper.update:
UPDATE STUDYCAFE
SET CAFE_NAME = #{cafeName}
	,CAFE_CONTENT = #{cafeContent}
	,CAFE_PHONE = #{cafePhone}
	,CAFE_ADDRESS = #{cafeAddress}
	,CAFE_IMG = #{cafeImg}
WHERE CAFE_NO = #{cafeNo}

UPDATE CAFEPRICE
SET C_ROOM_NAME = #{cRoomName}
	,C_ROOM_AVAILABLE_TIME = #{cRoomAvailableTime}
	,C_ROOM_TIME = #{cRoomTime}
	,C_ROOM_PRICE = #{cRoomPrice}
	,C_ROOM_HEAD_COUNT = #{cRoomHeadCount}
WHERE C_PRICE_NO = #{cPriceNo}

mapper.insert:
		MERGE INTO STUDYCAFE
		USING DUAL
		ON (CAFE_NAME = #{cafeName}
		  AND CAFE_CONTENT = #{cafeContent}
		  AND CAFE_PHONE = #{cafePhone}
		  AND CAFE_ADDRESS = #{cafeAddress})
		WHEN NOT MATCHED THEN
		INSERT (CAFE_NO
		  ,CAFE_NAME
		  ,CAFE_CONTENT
		  ,CAFE_PHONE
		  ,CAFE_ADDRESS
		  ,CAFE_IMG
		  ,CAFE_MAP_Y
		  ,CAFE_MAP_X)
		VALUES (SEQ_CAFE_NO.NEXTVAL
			  , #{cafeName}
			  , #{cafeContent}
			  , #{cafePhone}
			  , #{cafeAddress}
			  , #{cafeImg}
			  , NULL
			  , NULL)

mapper.select:
SELECT DISTINCT CAFE_NO
FROM STUDYCAFE
WHERE CAFE_NAME = #{cafeName} AND CAFE_CONTENT = #{cafeContent} AND CAFE_PHONE = #{cafePhone} AND CAFE_ADDRESS = #{cafeAddress}
