mapper.select:
SELECT * FROM BOARD JOIN MEMBER USING (MEMBER_NO)
AND B_CATEGORY_NO=#{bCategoryNo}
ORDER BY BOARD_NO DESC

SELECT * FROM BOARD
JOIN MEMBER USING (MEMBER_NO)
WHERE BOARD_NO=#{boardNo}

mapper.update:
UPDATE BOARD
SET BOARD_VCOUNT = BOARD_VCOUNT + 1
WHERE BOARD_NO=#{boardNo}

UPDATE BOARD
SET BOARD_TITLE=#{board.boardTitle}
	, BOARD_CONTENT=#{board.boardContent}
	, BOARD_MODIFY_DATE=SYSDATE
WHERE BOARD_NO=#{board.boardNo}

mapper.select:
SELECT * FROM REPLY JOIN MEMBER USING (MEMBER_NO)
AND B_CATEGORY_NO=#{bCategoryNo}
ORDER BY BOARD_NO DESC

mapper.insert:
INSERT INTO REPLY (REPLY_NO, BOARD_NO, B_CATEGORY_NO, MEMBER_NO, REPLY_CONTENT
					REPLY_CREATE_DATE, REPLY_MODIFY_DATE, REPLY_SELECTION,
					REPLY_STATUS, DECLARATION_COUNT)
VALUES(
	'RR' || LPAD(SEQ_REPLY_NO.NEXTVAL,5,0)
	, #{boardNo}
	, #{bCategoryNo}
	, #{memberNo}
	, #{replyContent}
	, SYSDATE
	, SYSDATE
	, 'N'
	, 'Y'
	, 0
)
