CREATE TABLE MEMBER_LEVEL (
	LVL_NO VARCHAR(255) CONSTRAINT PK_MEMBER_LEVEL PRIMARY KEY,
	LVL_RANK VARCHAR(255),
	LVL_MAX_EXP	VARCHAR(255)
);
COMMENT ON COLUMN MEMBER_LEVEL.LVL_NO IS '레벨번호(LVL00001)';
COMMENT ON COLUMN MEMBER_LEVEL.LVL_RANK IS '등급';
COMMENT ON COLUMN MEMBER_LEVEL.LVL_MAX_EXP IS '최대경험치';


CREATE TABLE MEMBER (
	MEMBER_NO VARCHAR2(100) CONSTRAINT PK_MEMBER PRIMARY KEY,
	MEMBER_EMAIL VARCHAR2(1000),
	MEMBER_PASSWORD	VARCHAR2(1000),
	MEMBER_NICKNAME	VARCHAR2(100),
	MEMBER_NAME	VARCHAR2(1000),
	MEMBER_GENDER VARCHAR2(2) CONSTRAINT CK_MEMBER_GENDER CHECK(MEMBER_GENDER IN('M', 'F')),
	MEMBER_PHONE VARCHAR2(100),
	LVL_NO VARCHAR2(100) DEFAULT 'LVL00001',
	MEMBER_EXP NUMBER,
	MEMBER_MEMO VARCHAR2(2000),
	MEMBER_PHOTO VARCHAR2(2000)	DEFAULT 'MEMBER_SAMPLE_IMG.JPG'	NOT NULL,
	MEMBER_ADMIN VARCHAR2(2) DEFAULT 'N' NOT NULL CONSTRAINT CK_MEMBER_ADMIN CHECK(MEMBER_ADMIN IN('Y', 'N')),
	MEMBER_STATUS VARCHAR2(2) DEFAULT 'N' NOT NULL CONSTRAINT CK_MEMBER_STATUS CHECK(MEMBER_STATUS IN('Y', 'N')),
	MEMBER_KAKAO VARCHAR2(2) DEFAULT 'N' CONSTRAINT CK_MEMBER_KAKAO CHECK(MEMBER_KAKAO IN('N', 'K')),
	MEMBER_JOIN_DATE DATE
);
COMMENT ON COLUMN MEMBER.MEMBER_NO IS '회원번호(M00001)';
COMMENT ON COLUMN MEMBER.MEMBER_EMAIL IS '이메일';
COMMENT ON COLUMN MEMBER.MEMBER_PASSWORD IS '비밀번호';
COMMENT ON COLUMN MEMBER.MEMBER_NICKNAME IS '닉네임';
COMMENT ON COLUMN MEMBER.MEMBER_NAME IS '이름';
COMMENT ON COLUMN MEMBER.MEMBER_GENDER IS '성별';
COMMENT ON COLUMN MEMBER.MEMBER_PHONE IS '휴대전화 번호';
COMMENT ON COLUMN MEMBER.LVL_NO IS '레벨번호(LVL00001)';
COMMENT ON COLUMN MEMBER.MEMBER_EXP IS '보유경험치';
COMMENT ON COLUMN MEMBER.MEMBER_MEMO IS '상태메시지';
COMMENT ON COLUMN MEMBER.MEMBER_PHOTO IS '프로필사진';
COMMENT ON COLUMN MEMBER.MEMBER_ADMIN IS '관리자:Y';
COMMENT ON COLUMN MEMBER.MEMBER_STATUS IS '탈퇴:Y 기본:N';
COMMENT ON COLUMN MEMBER.MEMBER_KAKAO IS '일반:N 카카오:K';
COMMENT ON COLUMN MEMBER.MEMBER_JOIN_DATE IS '가입날짜';

ALTER TABLE MEMBER ADD CONSTRAINT FK_MEMBER_LEVEL_TO_MEMBER FOREIGN KEY ( LVL_NO )
REFERENCES MEMBER_LEVEL ( LVL_NO );

