----- VIEW AREA ------------------------------------------------------------

CREATE OR REPLACE VIEW VIEW_MESSAGE AS
SELECT MESSAGE_NO, FROM_MEMBER_NO, MA.MEMBER_NICKNAME FROM_MEMBER_NICKNAME, MA.MEMBER_PHOTO FROM_MEMBER_PHOTO
	, TO_MEMBER_NO, MB.MEMBER_NICKNAME TO_MEMBER_NICKNAME, MB.MEMBER_PHOTO TO_MEMBER_PHOTO
	, MESSAGE_CONTENT, MESSAGE_DATE, FROM_M_CONFIRM, TO_M_CONFIRM
FROM MESSAGE MSG
LEFT JOIN MEMBER MA ON (MA.MEMBER_NO = MSG.FROM_MEMBER_NO)
LEFT JOIN MEMBER MB ON (MB.MEMBER_NO = MSG.TO_MEMBER_NO)
ORDER BY MESSAGE_DATE DESC;

-- MEMBER_MANAGE --

CREATE OR REPLACE VIEW MEMBER_MANAGE AS
SELECT 
  M.MEMBER_NO
, M.MEMBER_EMAIL
, M.MEMBER_NICKNAME
, M.MEMBER_NAME
, M.MEMBER_PHONE
, NVL(SUM(P.ADD_POINT),0) AS POINT
, (SELECT COUNT(*) FROM SANCTIONS S WHERE SANCTIONS_F_DATE >= SYSDATE AND S.MEMBER_NO=M.MEMBER_NO GROUP BY M.MEMBER_NO) AS SANCTIONS
FROM MEMBER M
LEFT JOIN POINT P ON (P.MEMBER_NO = M.MEMBER_NO)
WHERE M.MEMBER_STATUS = 'N'
GROUP BY M.MEMBER_NO, M.MEMBER_EMAIL, M.MEMBER_NICKNAME, M.MEMBER_NAME, M.MEMBER_PHONE
ORDER BY 1 DESC;

--CAFEEINFO--

CREATE OR REPLACE VIEW CAFEINFO AS
SELECT S.CAFE_NAME, S.CAFE_CONTENT, S.CAFE_ADDRESS, S.CAFE_PHONE, S.CAFE_IMG, C."C_PRICE_NO",C."CAFE_NO",C."C_ROOM_NAME",C."C_ROOM_AVAILABLE_TIME",C."C_ROOM_TIME",C."C_ROOM_PRICE",C."C_ROOM_HEAD_COUNT"
FROM STUDYCAFE S
LEFT JOIN CAFEPRICE C ON (S.CAFE_NO = C.CAFE_NO);

-- RESERVATION--

CREATE OR REPLACE VIEW RESERVATION AS
SELECT R."C_RESER_NO",R."MEMBER_NO",R."C_PRICE_NO",R."C_RESER_HEAD_COUNT",R."C_RESER_DATE",R."C_RESER_S_TIME",R."C_RESER_E_TIME", C.C_ROOM_NAME, C.C_ROOM_PRICE, S.CAFE_NO, S.CAFE_NAME, S.CAFE_PHONE, S.CAFE_ADDRESS
FROM CAFERESERVATION R
JOIN CAFEPRICE C ON (C.C_PRICE_NO = R.C_PRICE_NO)
JOIN STUDYCAFE S ON (S.CAFE_NO = C.CAFE_NO);

-- MENTO_MANAGE --

CREATE OR REPLACE VIEW METO_MANAGE AS
SELECT M.MEMBER_NO, M.MEMBER_EMAIL, M.MEMBER_NICKNAME, M.MEMBER_NAME, T.MENTOR_NO, T.MENTOR_PASS, S.SPEC_NO, S.SPEC_NAME, S.SPEC_FILENAME, SC.SPEC_C_NAME, S.SPEC_CONFIRM
FROM MEMBER M
JOIN MENTOR T ON (M.MEMBER_NO = T.MEMBER_NO)
JOIN SPEC S ON (M.MEMBER_NO = S.MEMBER_NO)
JOIN SPEC_CATEGORY SC ON (S.SPEC_C_NO = SC.SPEC_C_NO)
ORDER BY 1 ASC;

-- GROOMING_MANAGE --

CREATE OR REPLACE VIEW GROOMING_MANAGE AS
SELECT G.GROOMING_NO, G.GROOMING_TYPE, G.GROOMING_TITLE, G.GROOMING_INTRODUCE, G.GROOMING_PARTICIPANTS, G.GROOMING_CURRENT_PEOPLE, GC.G_CALENDAR_START_DATE, GB.G_BOARD_MODIFY_DATE
FROM GROOMING G
LEFT JOIN GROOMING_CALENDAR GC ON (G.GROOMING_NO = GC.GROOMING_NO)
LEFT JOIN GROMMING_MEMBER GM ON (G.GROOMING_NO = GM.GROOMING_NO)
LEFT JOIN GBOARD GB ON (GM.G_MEMBER_NO = GB.GROOMING_MEMBER_NO);

